class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        A = []
        for i in range(len(nums)):
            A.append(target[i] - nums[i])

        cur = 0
        i = 0 
        res = 0
        while i < len(A):
            val = A[i]
            if val > 0:
                if cur > 0:
                    if cur >= val:
                        cur = val
                    else:
                        res += val - cur
                        cur = val
                else:
                    res += val
                    cur = val
            elif val == 0:
                cur = val
            else:
                if cur < 0:
                    if cur <= val:
                        cur = val
                    else:
                        res += abs(cur - val)
                        cur = val
                else:
                    res += abs(val)
                    cur = val
            i += 1
        return res
