class Solution:
    def minimumOperations(self, nums, target) -> int:
        lastChg = 0
        result = 0

        for i in range(len(nums)):
            num = nums[i]
            targetNum = target[i]

            # decrement
            if num >= targetNum:
                decBy = -1 * (num - targetNum)
                # previous index decrements more, we can tag along
                if lastChg <= decBy:
                    pass
                elif lastChg >= 0:
                    result += -1 * decBy
                else:
                    result += -1 * (decBy - lastChg)

                lastChg = decBy

            # increment
            if num < targetNum:
                incBy = targetNum - num
                # previous index increments more, we can tag along
                if lastChg >= incBy:
                    pass
                elif lastChg <= 0:
                    result += incBy
                else:
                    result += (incBy - lastChg)
                lastChg = incBy

        return result
