class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> vec;
        for(auto i = 0; i < target.size(); ++i){
            vec.emplace_back(target[i]-nums[i]);
        }
        long long ans = labs(vec[0]);
        long long t = vec[0];
        for(int i = 1; i < vec.size(); ++i){
            if(vec[i]*t < 0){
                ans += labs(vec[i]);
                t = vec[i];
            }else {
                auto x = labs(vec[i]);
                auto y = labs(t);
                if(x == y){
                    continue;
                }else if(x > y){
                    ans += x - y;
                    t = vec[i];
                    //cout << i << " " << x << " " << y << "\n";
                }else{
                    t = vec[i];
                }
            }
            //cout << i << " " << ans << "\n";
        }
        return ans;

    }
};
