class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> v;
        for (int i = 0; i < nums.size(); i++) {
            v.push_back(nums[i] - target[i]);
            // cout << v[i] << ',';
        }
        // cout << '\n';
        long long res = 0;
        int j, value;

        for (int i = 0; i < nums.size(); i++) {
            // cout << v[i] << ',';
            if (v[i] > 0) {
                j = i + 1;
                value = v[i];
                while (j < nums.size() && v[j] > 0) {
                    if (v[j] < value) value = v[j];
                    v[j] -= value;
                    j++;
                }
                res += v[i];
            } else if (v[i] < 0) {
                j = i + 1;
                value = v[i];
                while (j < nums.size() && v[j] < 0) {
                    if (v[j] > value) value = v[j];
                    v[j] -= value;
                    j++;
                }
                res -= v[i];
            }
        }
        return res;
    }
};
