typedef long long ll;
class Solution {
public:
    Solution(){
        ios_base::sync_with_stdio(0);
        cin.tie(0);
        cout.tie(0);
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<ll> diff(n,0);
        for(int i=0;i<n;i++){
            diff[i]=0ll+target[i]-nums[i];
        }
        ll ans=0;
        ll prev=-1;
        while(prev!=ans){
            prev=ans;
            for(int i=0;i<n;){
                ll ma=INT_MIN,mi=INT_MAX;
                ll j=i;
                while(i<n && diff[i]>0){
                    mi=min(mi,diff[i]); i++;
                }
                while(j<i) diff[j++]-=mi;
                j=i;
                while(i<n && diff[i]<0){
                    ma=max(ma,diff[i]); i++;
                }
                while(j<i) diff[j++]-=ma;
                while(i<n && diff[i]==0) i++;
                if(ma!=INT_MIN) ans+=abs(ma);
                if(mi!=INT_MAX) ans+=abs(mi);
            }
        }
        return ans;
    }
};
