class Solution {
public:
    int a[100005];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++)a[i]=target[i]-nums[i];
        long long ans=0;
        ans+=abs(a[0]);
        for(int i=1;i<n;i++){
            if(a[i]>0&&a[i-1]>0){
                ans+=max(a[i]-a[i-1],0);
            }else if(a[i]<0&&a[i-1]<0){
                ans+=max(a[i-1]-a[i],0);
            }else ans+=abs(a[i]);
        }
        return ans;
    }
};
