class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        bool pos = true, neg = true;
        bool firstNeg = true, firstPos = true;
        long long prev = 0;
        for(int i = 0; i < nums.size(); i++) {
            long long value = nums[i] - target[i];
            if(value < 0) neg = true, pos = false;
            else neg = false, pos = true;

            if(neg && firstNeg) {
                firstPos = true;
                ans += abs(value);
                firstNeg = false;
                prev = value;
            } else if(neg) {
                if(abs(value) > abs(prev)) {
                    ans += ((abs(value) - abs(prev)));
                } 
                prev = value;
            }
            if(pos && firstPos) {
                firstNeg = true;
                ans += (value);
                firstPos = false;
                prev = value;
            } else if(pos) {
                if(value > prev) {
                    ans += (value- prev);
                }
                prev = value;
            }
        }
        return ans;
    }
};
