public class Solution {
    public long MinimumOperations(int[] nums, int[] target) {
        long ans =0;
        for(int i=0;i<nums.Length;i++)
        {
            nums[i]-=target[i];
            ans += Math.Abs(nums[i]);
        }
        for(int i=1;i<nums.Length;i++)
        {
            if(nums[i]>=0)
            {
                ans-=Math.Max(0,Math.Min(nums[i],nums[i-1]));
            }
            else if(nums[i]<0)
            {
                ans-=Math.Max(0,-Math.Max(nums[i],nums[i-1]));
            }
        }
        return ans;


    }
}
