class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        for (int i = 0; i < n; i++) {
            diff[i] = 1LL * (target[i] - nums[i]);
        }
        long long ans = 0, last = 0;
        for (int i = 0; i < n; i++) {
            if ((diff[i] <= 0 && last >= 0) || (diff[i] >= 0 && last <= 0)) {
                ans += llabs(diff[i]);
                last = diff[i];
            } else {
                if (llabs(diff[i]) > llabs(last)) {
                    ans += llabs(diff[i]) - llabs(last);
                }
                last = diff[i];
            }
        }
        return ans;
    }
};
