class Solution {
public:
    long long lol(vector<int>& a) {
        // for(auto i:a)cout << i << " ";cout << endl;
        stack<long long> s;
        long long ans = 0;

        s.push(a[0]);
        for (int i = 1; i < a.size(); i++) {
            if (a[i] > s.top()) {
                s.push(a[i]);
                continue;
            } 
            if (a[i] == s.top())
                continue;

            int top = s.top();

            while (s.size() && a[i] < s.top())
                s.pop();

            ans += top - a[i];
            s.push(a[i]);
        }

        if (s.size())
            ans += s.top();

        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int mode=(target[0]>nums[0]),s=0;
        for(int i=1;i<nums.size();i++)
        {
            if(mode==(target[i]>nums[i]))continue;
            vector<int> a;
            for(int j=s;j<i;j++)
            {
                if(mode==0) a.push_back(nums[j]-target[j]);
                else a.push_back(target[j]-nums[j]);
            }
            // cout << a.size() << " -> ";
            ans+=lol(a);
            mode=(target[i]>nums[i]);
            s=i;
        }

            vector<int> a;
            for(int j=s;j<nums.size();j++)
            {
                if(mode==0) a.push_back(nums[j]-target[j]);
                else a.push_back(target[j]-nums[j]);
            }
            // cout << a.size() << " -> ";
            ans+=lol(a);
        return ans;
    }
};
