class Solution {
public:
    long long calc(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;

        vector<long long> a(n);
        for (int i = 0; i < n; ++i) {
            a[i] = nums[i] - target[i];
        }
        ans += abs(a[0]);

        for (int i = 1; i < n; ++i) {
            if (a[i-1]<=0 && a[i]>0) ans+=a[i];
            else 
            if (a[i-1]>=0 && a[i]<0) ans+=-a[i];
            else {
                ans +=max(0LL, abs(a[i])-abs(a[i-1]));
            }
        }
        return ans;

    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long ans = calc(nums, target);
        reverse(nums.begin(), nums.end());
        reverse(target.begin(), target.end());
        return min(ans, calc(nums, target));
    }
};
