class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ret = 0;
        int n = nums.size();
        int p = 0;
        for(int i=0; i<n; ++i){
            int val = target[i] - nums[i];
            if(val > 0){
                if(p>=0)
                    ret += max(val - p, 0);
                else
                    ret += abs(val);
            }
            else if(val < 0){
                if(p<=0)
                    ret += max(p - val, 0);
                else
                    ret += abs(val);
            }
            p = val;
        }
        return ret;   
    }
};
