class Solution {
public:
    #define int long long
    int minNumberOperations(vector<int>& target) {
        // for(auto it : target) cout<<it<<" ";
        // cout<<"\n";
        int ans = target[0];
        int prev = target[0];
        for (int i = 1; i < target.size(); i++) {
            if (target[i] > prev) {
                ans += target[i] - prev;
            }
            prev = target[i];
        }
        return ans;
    }
    #undef int
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        #define int long long
        vector<int> help;
        for(int i=0;i<nums.size();i++){
            help.push_back(nums[i]-target[i]);
        }
        int i = 0;
        int ans = 0;
        vector<int> keep;
        while(i<nums.size()){
            keep.push_back(abs(help[i]));
            if(i+1<nums.size()){
                if(help[i+1]>=0 && help[i]>=0 || help[i+1]<=0 && help[i]<=0)
                    i++;
                else{
                    ans += minNumberOperations(keep);
                    keep.clear();
                    i++;
                }
            }
            else i++;
        }
        if(keep.size()) ans += minNumberOperations(keep);
        return ans;
        #undef int
    }
};
