class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        // +1 +1 +1 +2
        // +1 -2 +2
         int n = nums.size();
    long long ans = 0;
    long long prev = 0;

    for (int i = 0; i < n; ++i) {
        int diff = target[i] - nums[i];

        if(diff<0 && prev>0 && diff!=0)
            ans+=abs(diff);
        else if(diff>0 && prev<0 && diff!=0)
            ans+=abs(diff);
        else if(diff!=0 && abs(diff)>abs(prev))
        ans += abs(diff - prev);

        prev = diff;
    }

    return ans;
    }
};
