using LL = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> f(n);
        vector<bool> st(n);
        for(int i = 0; i < n; i ++ ){
            f[i] = b[i] - a[i];
            if(f[i] < 0){
                st[i] = 1;
                f[i] = -f[i];
            }
        }
        int i, j = 0;
        bool fg = st[0];
        LL ans = 0;
        auto ck = [&](){
            ans += f[j];
            for(int k = j + 1; k < i; k ++ ){
                if(f[k] > f[k - 1]) ans += f[k] - f[k - 1];
            }
        };

        for(i = 1; i < n; i ++ ){
            if(st[i] != fg){
                ck();
                j = i;
                fg = st[i];
            }
        }
        ck();
        return ans;
    }
};
