class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i=0;i<nums.size();++i)
        {
            nums[i] = target[i]-nums[i];
        }


        vector<int64_t> dp(nums.size(), 0);
        dp[0] = abs(nums[0]);
        for(int i=1;i<nums.size();++i)
        {
            if( (int64_t)nums[i]*(int64_t)nums[i-1] <= 0 )
            {
                dp[i] = dp[i-1] + abs(nums[i]);
            }
            else
            {
                dp[i] = dp[i-1] + max(0,abs(nums[i])-abs(nums[i-1]) );
            }
        }
        return dp[nums.size()-1];
    }
};
