class Solution {
public:
typedef long long ll;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        vector<ll> arr(n);
        for(int i = 0; i < n; i++){
            arr[i] = target[i] - nums[i];
        }
        ll ans = 0;
        ll a = 0, b = 0, f = 0;
        for(int i = 0; i < n; i++){
            if(arr[i] >= 0){
                if(!f or i == 0){
                    ans += arr[i];
                }else if(arr[i] > b){
                    ans += (arr[i] - b);
                }
                b = arr[i];
                f = 1;
            }else{
                if(f or i == 0){
                    ans += abs(arr[i]);
                }else if(arr[i] < b){
                    ans += (b - arr[i]);
                }
                b = arr[i];
                f = 0;
            }
        }
        return ans;
    }
};
