class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long pre=0,div=0;
        long long ans=0;
        for(int i=0;i<n;++i){
            int div=nums[i]-target[i];
            // cout<<div<<" "<<pre<<"\n";
            if(pre*div>0){
                ans+=max(0ll,abs(div)-abs(pre));
            }
            else ans+=abs(div);
            pre=div;
        }
        return ans;
    }
};
