class Solution {
public:
    long long f(vector<int>& target)
    {
        for(int i=0;i<target.size();i++)
        {
            target[i]=abs(target[i]);
        }
        long long res = 0;
        vector<int> stack;
        stack.push_back(target[0]);
        for (int i = 1; i < target.size(); i++) {

            if (target[i] > stack.back()) {
                stack.push_back(target[i]);
                continue;
            } 
            else{

            if (target[i] == stack.back())
                continue;
            else{     

            int top = stack.back();

            while (!stack.empty() && target[i] < stack.back())
                stack.pop_back();

                res += (top - target[i]);
                stack.push_back(target[i]);
            }
            }
        }

        if (!stack.empty())
            res += stack.back();

          return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
         int n=nums.size();
         vector<vector<int>>v;
         vector<int>dif(n);
         for(int i=0;i<n;i++)
         {
            dif[i]=target[i]-nums[i];
         }
         long long ans=0;
         for(int i=0;i<n;i++)
         {
            if(dif[i]==0)
            {
                continue;
            }
            vector<int>pre;
            pre.push_back(dif[i]);
            int ind=n;
            for(int j=i+1;j<n;j++)
            {
               if(dif[i]<0 && dif[j]<0)
               {
                  pre.push_back(dif[j]);
               } 
               else
               {
                    if(dif[i]>0 && dif[j]>0)
                    {
                        pre.push_back(dif[j]);
                    } 
                    else{
                        ind=j;
                        break;
                    }
               }
            }
            ans+=f(pre);
            i=(ind-1);
         }
         return ans;

    }
};
