class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def sgn(x):
            if x == 0:
                return 0
            if x > 0:
                return 1
            if x < 0:
                return -1

        def abs(x):
            if x >= 0:
                return x
            else:
                return -x

        def f(x, y):
            for i in range(x, y):
                a[i] = abs(a[i])
            if a[i] == 0:
                return 0
            if y - x < 3:
                return max(a[x:y])
            b = a[x:y]
            ans = 0
            for i in range(1, len(b)):
                if b[i] < b[i - 1]:
                    ans += b[i - 1] - b[i]
            ans += b[-1]
            return ans


        a = [nums[i] - target[i] for i in range(len(nums))]
        #print(a)
        res = 0
        l = 0
        for i in range(len(a) - 1):
            if sgn(a[i])== sgn(a[i + 1]):
                continue
            else:
                res += f(l, i+1)
                l = i + 1
        return res + f(l, len(a))
