class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        cans = 0
        l = []
        for i,n in enumerate(nums):
            l.append(n-target[i])
        cdir = 0
        cmv=0
        csame=0
        #print(l)
        for i,n in enumerate(l):
            if n>0:
                if cdir<0:
                    cans+=n
                    csame=n
                    cmv=n
                elif cdir==0:
                    csame=n
                    cmv=n
                    cans+=n
                else:
                    if n>cmv:
                        cans+=(n-cmv)
                        cmv=n
                    else:
                        cmv = min(n,cmv)
                    if n<csame:
                        csame=n
                cdir=1
            elif n<0:
                nb = abs(n)
                if cdir>0:
                    cans+=nb
                    csame=nb
                    cmv=nb
                elif cdir==0:
                    csame=nb
                    cmv=nb
                    cans+=nb
                else:
                    if nb>cmv:
                        cans+=(nb-cmv)
                        cmv=nb
                    else:
                        cmv = min(nb,cmv)
                    if nb<csame:
                        csame=nb
                cdir=-1
            else:
                cdir=0
                cmv=0
                csame=0
            #print(cdir,cmv,csame,cans)
        return cans
