class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        for i in range(n) :
            nums[i] = target[i] - nums[i]
        nums.insert(0 , 0 )
        cnt1 , cnt2 = 0 , 0 
        b = [0] * (n + 10)
        for i in range(1 ,n + 1) :
            b[i] = nums[i] - nums[i - 1]
        for i in range(1 , n + 1) :
            if b[i] > 0 :
                cnt1 += b[i]
            if b[i] < 0 :
                cnt2 -= b[i]
        return max(cnt1 , cnt2)
