class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [y - x for x, y in zip(nums, target)]

        uniques = []
        prev = math.inf
        for x in diff:
            if x != prev:
                uniques.append(x)

            prev = x

        ans = 0
        built = 0
        # print("NEW")
        for x in uniques:
            prev_built = built
            if x > 0:
                if x <= built:
                    built = x
                else:
                    ans += min(x, x - built)
                    built = x
            else:
                if x >= built:
                    built = x
                else:
                    ans += min(-x, -x + built)
                    built = x

            # print(prev_built, x)
        return ans
