#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& A, vector<int>& B) {
        ll N = A.size();
        vector<ll> D(N);
        for(int i = 0; i < N; ++i)
            D[i] = B[i] - A[i];
        ll lst = 0;
        ll ans = 0;
        for(ll i = 0; i < N; ++i) {
            if(D[i] == 0) {
                lst = 0;
                continue;
            }
            if(D[i] > 0) {
                if(lst < 0) lst = 0;
                if(D[i] > lst) ans += D[i] - lst;
                lst = D[i];
            } else {
                if(lst > 0) lst = 0;
                if(D[i] < lst) ans += lst - D[i];
                lst = D[i];
            }
        }
        return ans;
    }
};
