class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans=0, prev=0;
        for(int i=0;i<nums.length;i++){
            if(prev==0){
                prev=nums[i]-target[i];
                ans+=Math.abs(prev);
            }else{
                long temp=nums[i]-target[i];
                if(temp<0 && prev<0){
                    ans+=Math.abs(Math.min(temp-prev,0));
                    prev=temp;
                }else if(temp>0 && prev>0){
                    ans+=Math.abs(Math.max(temp-prev,0));
                    prev=temp;
                }else{
                    prev=temp;
                    ans+=Math.abs(prev);
                }
            }
        }
        return ans;
    }
}
