class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        vector<int> va(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            va[i] = nums[i] - target[i];
        }

        int curr = 0;
        for (int i = 0; i < nums.size(); i++) {
            int v = va[i];

            if (v == 0) {
                res += abs(curr);
                curr = 0;
            } else if (curr == 0) {
                curr = v;
            } else if (curr < 0) {
                if (v < 0) {
                    if (v <= curr) {
                        curr = v;
                    } else {
                        res += abs(v - curr);
                        curr = v;
                    }
                } else {
                    res += abs(curr);
                    curr = v;
                }
            } else {
                if (v > 0) {
                    if (v >= curr) {
                        curr = v;
                    } else {
                        res += abs(curr - v);
                        curr = v;
                    }
                } else {
                    res += abs(curr);
                    curr = v;
                }
            }

            // cout << curr << " /" << res << endl;
        }

        res += abs(curr);

        return res;
    }
};
