class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        if not len(nums):
            return 0
        n = len(nums)
        xs = [x - y for x, y in zip(nums, target)]

        ans = xs[0]if xs[0] >= 0 else -xs[0]
        for i in range(1, n):
            if xs[i] > 0:
                pre = max(xs[i-1], 0)
                if xs[i] > pre:
                    ans += xs[i] - pre 
            else:
                pre = max(-xs[i-1], 0)
                if -xs[i] > pre:
                    ans += -xs[i] - pre 
        return ans
