class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        new = []
        sign = None
        for x, y in zip(nums, target):
            diff = y - x
            new_sign = (diff >= 0)
            if sign is not new_sign:
                new.append(0)
            sign = new_sign
            new.append(diff)
        new.append(0)
        stack = []
        ans = 0
        for n in new:
            n = abs(n)
            if stack and stack[-1] >= n:
                ans += stack.pop() - n
                while stack and stack[-1] >= n:
                    stack.pop()
            stack.append(n)
        return ans
