class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, res = 0;
        for (int i = 0; i < nums.size(); ++i) {
            //cout << ans << ' ' << res << '\n';
            if (nums[i] <= target[i] && res < 0) res = 0;
            if (nums[i] >= target[i] && res > 0) res = 0;

            int c = target[i] - nums[i];
            if (c >= 0) if (res < c) ans += c - res;
            if (c < 0) if (res > c) ans += res - c;
            res = c;
        }
        return ans;
    }
};
