class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        n = len(nums)
        old_diff = 0
        ans = 0
        for i in range(n):
            diff = nums[i] - target[i]
            if (diff >= 0 and old_diff <= 0) or (diff <= 0 and old_diff >= 0):
                ans += abs(diff)
                old_diff = diff
            else:
                ans += max(0, abs(diff) -abs(old_diff))
                old_diff = diff

        return ans
