class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            nums[i] -= target[i];
            ans += max(0, nums[i] - (i == 0 ? 0 : nums[i - 1]));
        }
        return ans + max(0, -nums[n - 1]);
    }
};
