class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> temp(n);

        int mini = 1e9;
        for(int i = 0; i < n ; i++) {
            temp[i] = target[i] - nums[i];
            mini = min(mini, temp[i]);
        }

        long long pans = 0;
        // if(mini > 0) {
        //     ans = mini;
        //     for(int i = 0; i < n ; i++) {
        //         temp[i] -= mini;
        //         cout<<temp[i]<<" ";
        //     }
        //     cout<<"\n";
        // }

        for(int i = 0; i < n ; i++) cout<<temp[i]<<" ";
        cout<<"\n";

        ans += abs(temp[0]);
        // int last_val = temp[0];
        for(int i = 1; i < n ; i++) {
            if(temp[i] > 0 && temp[i-1] > 0){
                if(temp[i] > temp[i-1]) {
                    ans += (temp[i]-temp[i-1]);
                }
            }
            else if(temp[i] < 0 && temp[i-1] < 0) {
                if(temp[i] < temp[i-1]) {
                    ans += abs(temp[i]-temp[i-1]);
                }
            }
            else{
                ans += abs(temp[i]);
            }
        }
        return ans;
    }
};
