class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ret = 0
        last = 0
        for i in range(len(nums)):
            cur = target[i] - nums[i]
            if cur > 0:
                if last < cur:
                    if last < 0: ret += cur
                    else: ret += cur-last
            if cur < 0:
                if last > cur:
                    if last < 0: ret += abs(cur-last)
                    else: ret += -cur
            last = cur
        return ret
