class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long cnt = 0;
        vector<int> temp(nums.size()+2, 0);
        vector<int> diff(nums.size()+2, 0);
        for(int i=0;i<nums.size();++i){
            temp[i+1] = nums[i] - target[i];
        }
        for(int i=1;i<nums.size()+2;++i){
            diff[i] = temp[i] - temp[i-1];
        }
        for(int i:diff){
            if(i>0){
                cnt += i;
            }
        }
        return cnt;
    }
};
