class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        target = [t - n for (n, t) in zip(nums, target)]
        ans = pre = 0
        for num in target:
            if min(num, pre) >= 0:
                ans += max(num - pre, 0)
            elif max(num, pre) <= 0:
                ans += -min(num - pre, 0)
            else:
                ans += abs(num)
            pre = num
        return ans
