class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans1=0,ans2=0;
        int n=nums.size();
        for(int i=n-1;i>=0;i--){
            if(i==0){
                if(nums[i]<target[i])ans1+=abs(nums[i]-target[i]);
                else ans2+=abs(nums[i]-target[i]);
            }
            else{
                nums[i]-=nums[i-1];
                target[i]-=target[i-1];
                if(nums[i]<target[i])ans1+=abs(nums[i]-target[i]);
                else ans2+=abs(nums[i]-target[i]);
            }
        }
        return max(ans1,ans2);
    }
};
