class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        s = [t - a for a, t in zip(nums, target)]
        n = len(s)
        ans = i = 0
        while i < n:
            if s[i] == 0:
                i += 1
                continue
            j = i
            mx = mn = abs(s[i])
            while j + 1 < n and s[j + 1] * s[i] > 0:
                j += 1
                d = abs(s[j])
                if mx == mn and d >= mx:
                    mx = mn = d
                elif d <= mn:
                    mn = d
                else:
                    ans += mx - mn
                    mx = mn = d
            ans += mx
            i = j + 1

        return ans
