class Solution:

    def solve(self, lst, n):

        # print(n, lst)

        if n == 0:
            return n

        rslt = lst[0]

        for x in range(1, n):
            if lst[x] > lst[x - 1]:
                rslt += (lst[x] - lst[x - 1])

        return rslt

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        n = len(nums)

        lst = []
        rslt = 0

        for x in range(n):
            lst.append(target[x] - nums[x])

        nums = lst
        # print(nums)
        x = 0

        while x < n:
            tmp = []
            while x < n and nums[x] < 0:
                tmp.append(-nums[x])
                x += 1

            rslt += self.solve(tmp, len(tmp))
            tmp = []

            while x < n and nums[x] == 0:
                x += 1

            while x < n and nums[x] > 0:
                tmp.append(nums[x])
                x += 1

            rslt += self.solve(tmp, len(tmp))

        # print()
        return rslt
