class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        for i in range(n):
            nums[i] = target[i] - nums[i]
        ans = 0
        ans += max(nums[0], -nums[0])
        for i in range(1, n):
            if nums[i] > 0:
                if nums[i - 1] > 0:
                    ans += max(0, nums[i] - nums[i - 1])
                else:
                    ans += nums[i]
            if nums[i] < 0:
                if nums[i - 1] < 0:
                    ans += max(0, (-nums[i]) + nums[i - 1])
                else:
                    ans += -nums[i]
        return ans
