class Solution {
public:
    long long minimumOperations(vector<int>& arr, vector<int>& a) {

        int n = arr.size() , i , j;
        long long ans = 0;

        vector<long long> d(n);
        for(i = 0;i< n;i++)
            d[i] = a[i] - arr[i];

        long long mn = 0 , mx = 0 , lmn = 0, lmx = 0;
        for(i = 0;i< n;i++){
            if (d[i] < 0){
                // if (lmx < mx)
                //     ans += mx - lmx;
                mx = lmx = 0;
                if (mn > d[i])
                    ans += mn - d[i];
                lmn = mn;
                mn = d[i];
            } else if (d[i] > 0) {
                // if (lmn > mn)
                //     ans += lmn - mn;
                if (mx < d[i])
                    ans += d[i]-mx;
                mn = lmn = 0;
                lmx = mx;
                mx = d[i];
            } else {
                mn = mx = lmn = lmx = 0;
                // if (mn == 0){
                //     if (lmx < mx)
                //         ans += mx - lmx;
                //     mx = lmx = 0;
                // } else {
                //     if (lmn > mn)
                //         ans += lmn - mn;
                //     mn = lmn = 0;
                // }
            }

            // cout << ans << " ";
        }

        return ans;
    }
};
