/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    let n = nums.length;
    let diff = [];
    for (let i = 0; i < n; i++) {
        diff.push(target[i] - nums[i]);
    }
    diff.push(-1);

    let stack = [];
    let res = 0;
    // console.log(diff);
    for (let i = 0; i < diff.length; i++) {
        let cur = diff[i];
        if (stack.length && stack[stack.length - 1][0] === cur) continue;
        if (cur <= 0 && stack.length) {
            let max = 0;
            while (stack.length) {
                let [val, index] = stack.pop();
                max = Math.max(max, val);
            }
            // console.log(max, "as");
            res += max;
            console.log(i, max);
        }
        if (cur > 0 && stack.length) {
            let max = -1;
            while (stack.length && stack[stack.length - 1][0] > cur) {
                let [val, index] = stack.pop();
                max = Math.max(max, val);
            }     
            // console.log(max, cur, "asf");
            if (max !== -1) {
                res += max - cur;
                console.log(i, max - cur);
            }
        }
        // console.log(res);

        if (cur > 0) {
            stack.push([cur, i]);
        }
    }
    stack = [];
    diff.pop();
    diff.push(1);
    for (let i = 0; i < diff.length; i++) {
        let cur = diff[i];
        if (stack.length && stack[stack.length - 1][0] === cur) continue;
        if (cur >= 0 && stack.length) {
            let min = 0;
            while (stack.length) {
                let [val, index] = stack.pop();
                min = Math.min(min, val);
            }
            res += -min;
            // console.log(i, -min);
        }
        if (cur < 0 && stack.length) {
            let min = 1;
            while (stack.length && stack[stack.length - 1][0] < cur) {
                let [val, index] = stack.pop();
                min = Math.min(min, val);
            }     
            if (min !== 1) {
                res += cur - min;
                // console.log(i, cur - min, cur, min);
            }
        }

        if (cur < 0) {
            stack.push([cur, i]);
        }
    }

    return res;
};

