class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        //使用栈
        //以 正数为例，一旦上升就入栈，一旦下降就削平山头，一旦遇到0或反向数字就+一个最大值就把前面都解决了
        long long ans=0;
        const int n=nums.size();
        vector<int> sub(n);
        for(int i=0;i<n;i++){
            sub[i]=nums[i]-target[i];
        }
        int currentMax=0;

        for(int i=0;i<n;i++){
            if(sub[i]>=0 && currentMax<=0){
                ans -=currentMax;
                currentMax=sub[i];
                continue;
            }
            if(sub[i]<=0 && currentMax>=0){
                ans+=currentMax;
                currentMax=sub[i];
                continue;
            }
            if(sub[i]>=0 && currentMax>=0){
                if(sub[i]>=currentMax) currentMax=sub[i];
                else { ans += (currentMax-sub[i]);
                    currentMax=sub[i];}
                    continue;
            }
            if(sub[i]<=0 && currentMax<=0){
                if(sub[i]<=currentMax) currentMax=sub[i];
                else {ans -= (currentMax-sub[i]);
                currentMax=sub[i];}
                continue;
            }
        }
        return ans+abs(currentMax);
    }
};
