class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        int n = nums.size();
        int temp = 0;
        for(int i = 0;i<n;i++){
            int val = target[i] - nums[i];
            if((temp<0 && val<0) || (temp>0 && val>0)){
                if(abs(temp) <= abs(val)){
                    res += abs(val) - abs(temp);
                    temp = val;
                }
                else{
                    temp = val;
                }
            }
            else{
                res += abs(val);
                temp = val;
            }
        }

        return res;
    }
};
