class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        pre = target[0] - nums[0]
        n = len(target)
        ans = abs(pre)
        for i in range(1, n):
            tmp = target[i] - nums[i]
            if tmp == 0:
                pre = 0
            elif pre == 0 or pre*tmp < 0:
                ans += abs(tmp)
                pre = tmp
            else:
                if tmp > 0:
                    if tmp > pre:
                        ans += tmp - pre
                        pre = tmp
                    else:
                        pre = tmp
                else:
                    if tmp > pre:
                        pre = tmp
                    else:
                        ans += pre - tmp
                        pre = tmp
        return ans
