class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<int> v(nums.size(),0);
        for(int i = 0; i < nums.size();i++)
        {
            v[i] = target[i] - nums[i];
        }
        for(int i = 0; i < v.size();)
        {
            if(v[i]==0)
            {
                i++;
                continue;
            }
            else if(v[i] < 0)
            {
                int mn = -v[i];
                ans += mn;
                i++;
                while(i < v.size() && v[i] < 0)
                {
                    if(mn < (-v[i]) )
                    {
                        ans += -v[i] - mn;
                    }
                    mn = -v[i];
                    i++;
                } 
            }
            else{
                int mn = v[i];
                ans += mn;
                i++;
                while(i < v.size() && v[i] > 0)
                {
                    if(mn < (v[i]) )
                    {
                        ans += v[i] - mn;
                    }
                                            mn = v[i];

                    i++;
                }
            }
        }

        return ans;
    }
};
