class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = n-1;i >= 1;i --)
        {
            nums[i] -= nums[i-1];
            target[i] -= target[i-1];
        }
        vector<int> dif(n);
        long long ans = 0;
        long long neg = 0,pos = 0;
        for(int i = 0;i < n;i ++)
        {
            dif[i] = nums[i] - target[i];
            // cout << dif[i] << endl;
            if(dif[i] < 0)neg -= dif[i];
            else pos += dif[i];
        }
        ans = max(neg,pos);
        return ans;


    }
};
