class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a - b for a, b in zip(nums, target)]
        curr = ans = 0
        for num in diff:
            if num > 0:
                curr = max(0, curr)
                if num > curr:
                    ans += num - curr
                curr = num
            else:
                curr = min(0, curr)
                if num < curr:
                    ans += curr - num
                curr = num
        return ans
