class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0, n = nums.size();
        vector<int> v(n);
        for(int i=0; i<n; i++)v[i] = target[i] - nums[i];
        // for(auto it:v)cout<<it<<' ';
        cout<<endl;
        int ct = 0;
        for(int i=0;i<n;i++){
            if(v[i] == 0)ct = 0;
            else if(v[i] > 0){
                if(v[i] <= ct){
                    ct = v[i];
                    continue;
                }
                else{
                    if(ct < 0)ct = 0;
                    res += v[i] - ct;
                    ct  += v[i] - ct;
                }
            }else{
                if(ct > 0)ct = 0;
                if(ct <= 0){
                    if(v[i] >= ct){
                        ct = v[i];
                        continue;
                    }
                    else{
                        res += abs(ct - v[i]);
                        ct -= abs(ct - v[i]);
                    }
                }
            }
        }
        return res;
    }
};
