class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        sub = [target[i]-nums[i] for i in range(n)]
        dp = [0]*n
        dp[0]=abs(sub[0])
        for i in range(1,n):
            if sub[i]*sub[i-1]>=0:
                if abs(sub[i])<=abs(sub[i-1]):
                    dp[i]=dp[i-1]
                else:
                    dp[i]=dp[i-1]+abs(sub[i]-sub[i-1])
            else:
                dp[i]=dp[i-1]+abs(sub[i])
        return dp[n-1]
