class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        stack<int> pos, neg;
        pos.push(0);
        neg.push(0);

        int n = nums.size();
        long long ans = 0;
        for (int i = 0; i < n; ++i)
        {
            long long diff = target[i] - nums[i];

            if (diff > 0)
            {
                ans -= neg.top();
                while (neg.top() != 0)
                {
                    neg.pop();
                }

                if (pos.top() >= diff)
                {
                    ans += pos.top() - diff;
                }

                while (pos.top() >= diff)
                {
                    pos.pop();
                }

                pos.push(diff);
            }

            if (diff < 0)
            {
                ans += pos.top();
                while (pos.top() != 0)
                {
                    pos.pop();
                }

                if (neg.top() <= diff)
                {
                    ans += diff - neg.top();
                }

                while (neg.top() <= diff)
                {
                    neg.pop();
                }

                neg.push(diff);
            }

            if (diff == 0)
            {
                ans += pos.top();
                while (pos.top() != 0)
                {
                    pos.pop();
                }

                ans -= neg.top();
                while (neg.top() != 0)
                {
                    neg.pop();
                }
            }

            cout << diff << " " << ans << endl;
        }

        ans += pos.top();
        ans -= neg.top();

        return ans;
    }
};
